import './Navbar.css';
import { Link, animateScroll as scroll} from 'react-scroll';
import { useInView } from 'react-intersection-observer';


function NavBar(){
    const [activeLink, setActiveLink] = useState('home');

    const [refHome, inViewHome] = useInView({
      triggerOnce: true,
    });
  
    const [refAbout, inViewAbout] = useInView({
      triggerOnce: true,
    });
  
    const [refSkills, inViewSkills] = useInView({
      triggerOnce: true,
    });
  
    const [refProjects, inViewProjects] = useInView({
      triggerOnce: true,
    });
  
    const [refContacts, inViewContacts] = useInView({
      triggerOnce: true,
    });
  
    const handleSetActive = (link) => {
      setActiveLink(link);
    };
  
    return(
        <nav>
            <ul>
                <li><Link to="home" smooth={true} duration={500} onClick={() => handleSetActive('home')}
            active={activeLink === 'home'} ref={refHome}>Home</Link></li>
                <li><Link to="about" smooth={true} duration={500} onClick={() => handleSetActive('about')}
            active={activeLink === 'about'} ref={refAbout}>About</Link></li>
                <li><Link to="skills" smooth={true} duration={500}onClick={() => handleSetActive('skills')}
            active={activeLink === 'skills'} ref={refSkills}>Skills</Link></li>
                <li><Link to="projects" smooth={true} duration={500}onClick={() => handleSetActive('projects')}
            active={activeLink === 'projects'} ref={refProjects}>Projects</Link></li>
                <li><Link to="home" smooth={true} duration={500}onClick={() => handleSetActive('contacts')}
            active={activeLink === 'contacts'} ref={refContacts}>Contacts</Link></li>
            </ul>
        </nav>
    );
}

export default NavBar
